function favorite(state =[], action) {
  switch (action.type) {
   case ADD_TO_FAVORITES:
     return Object.assign({}, state, {
       isFavorite: action.flag
     })
   case REMOVE_FROM_FAVORITES:
     return Object.assign({}, state, {
       todos: [
         ...state.todos,
         {
           text: action.text,
           completed: false
         }
       ]
     })
   default:
     return state
 }
}

export default favorite;
