import React from 'react';
import { Link } from 'react-router-dom';
import './CardModal.css';
import cardIcon from '../../../assets/cardIcon.jpg';
import heart from '../../../assets/heart.svg';
import activeHeart from '../../../assets/activeHeart.svg';

class CardModal extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isFavorite: true
    }
    this.handleHeartClick = this.handleHeartClick.bind(this);
  }

  handleHeartClick(e) {
    e.preventDefault();
    this.setState({ isFavorite: !this.state.isFavorite });
  }

render() {
  const data = this.props.data;
  const index = this.props.match.params.id.slice(1);
  const item = data && data[index];
  const headline = item ? item.name ? item.name : item.title : null;
  const year = item ? item.birth_year ? item.birth_year : item.year : null;
  const episodeId = this.props.episodeId;
  const heartIcon = this.state.isFavorite ? activeHeart: heart;
  const {view, films, i, people} = this.props;
  return (
    <div className="container">
    <div className='card-modal'>
    <div className='headline'>
     {headline}
     </div>
     <div className='year'>
      {year}
      </div>
    <img src={cardIcon} alt="cardIcon" />
    <div onClick={this.handleHeartClick}>
    <img src={heartIcon} className='heart' alt="cardIcon" />
    </div>

     {
      episodeId ?
      <div className='episode'>Episode: {item && item.episodeId}</div> :
      <div className='episode'>Gender :  {item && item.gender}</div>
      }
     <div className='episode'>{item && item.director}</div>
   </div>
   </div>
 );
};
};

export default CardModal;
