import React from 'react';
import { connect } from 'react-redux'
import { Link } from 'react-router-dom';
import './SingleCard.css';
import cardIcon from '../../../assets/cardIcon.jpg';
import heart from '../../../assets/heart.svg';
import activeHeart from '../../../assets/activeHeart.svg';
import { favoriteCard } from '../../../actions/actionCreators';

class SingleCard extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isFavorite: false
    }
    this.handleHeartClick = this.handleHeartClick.bind(this);
  }

  handleHeartClick(e) {
    e.preventDefault();
    debugger;
    let state = this.props.getState()
    let flag = !this.state.isFavorite;
     state = this.state;
    this.props.favoriteCard(state,flag);
  //  this.setState({ isFavorite: !this.state.isFavorite });
  }

render() {
  const episodeId = this.props.episodeId;
  const heartIcon = this.state.isFavorite ? activeHeart: heart;
  const {view, films, i, people} = this.props;
  return (
<Link to={`/:${view}/:${i}`}>
    <div className='card'>
    <img src={this.props.cardIcon} alt="cardIcon" />
    <div >
    <img src={heartIcon} className='heart' alt="cardIcon" onClick={this.handleHeartClick}/>
    </div>
    <div className='title'>
     {this.props.title ? this.props.title : this.props.name}
     </div>
     {
      episodeId ?
      <div className='episode'>Episode: {this.props.episodeId}</div> :
      <div className='episode'>Gender :  {this.props.gender}</div>
      }
     <div className='director'>{this.props.director}</div>
   </div>
   </Link>
 );
};
};

const mapStateToProps = (state, ownProps) => ({
  isFavorite: ownProps.isFavorite === state.isFavorite
})

const mapDispatchToProps = dispatch => ({
  favoriteCard: (state, flag) => dispatch(favoriteCard(state, flag))
})

SingleCard = connect(mapStateToProps, mapDispatchToProps)(SingleCard)
export default SingleCard;
