import React from 'react';
import SingleCard from '../SingleCard/SingleCard';
import { films }  from '../../../data/films';
import { people }  from '../../../data/people';
import './CardsGrid.css';


class CardsGrid extends React.Component {
  constructor(props) {
    super(props);
    let avatars = {};
  }

  componentWillMount () {
      let images = [];
       let req = require.context('../../../assets/avatars', true, /.*\.png$/);
      this.avatars = req.keys();
     }

       componentDidMount() {
         this.props.fetchDataFromAPI('films');
       }

    // const param = this.props.data;
    //{ useState, useEffect }
    // fetch(`https://swapi.co/api/people`)
    //   .then((data) => {
    //     this.setState({ data });
    //   })
  // }

  render() {
    this.cardIcons = this.avatars || '';
    const data = this.props.data1 || [];
    const view = this.props.data;
    debugger;
    return (
      <div>
      <ul className="flex-container wrap">
      {data ? data.map((item, index)  => {
        let icon = this.avatars ? './avatars/'+ this.avatars[index].slice(2) : null;
        return (<li key={index}>
                <SingleCard
                    cardIcon={icon}
                    i={index}
                    view={view}
                    title={item.title}
                    episodeId={item.episode_id}
                    director={item.director}
                    name={item.name}
                    gender={item.gender}
                    favorite={this.props.favorite}
                    />
                  </li>);
                }) : null}
      </ul>
      </div>
    );
  }
};

export default CardsGrid;
