import axios from "axios";

function fetchAPI(state =[], action) {
  switch (action.type) {
   case 'SET_DATA':
   debugger;
    let obj = {};
    // let param = action.param || 'data';
    // obj[param] = action.payload;
    obj.data = action.payload;
    return Object.assign({}, state, obj);
   default:
     return state
 }
};

export default fetchAPI;

export function fetchDataFromAPI(param) {
 return function(dispatch) {
   return axios.get(`https://swapi.co/api/${param}`).then(({ data }) => {
     debugger;
     dispatch(setDataFromAPI(data.results));
   });
 };
};
