function fetchAPI(state =[], action) {

  switch (action.type) {
   case 'SET_DATA':
    let obj = {};
    obj[action.param] = action.payload;
    return Object.assign({}, state, obj);
   default:
     return state
 }
}

export default fetchAPI;
