export function favoriteCard (isFavorite) {
  return {
    type: 'TOGGLE_FAVORITES',
    isFavorite
  }
};

export function unfavoriteCard (index) {
  return {
    type: 'REMOVE_FROM_FAVORITES',
    index
  }
};
